import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import './RecommendationPage.css';

const sampleProducts = [
  {
    name: 'HydraGlow Serum',
    description: 'Brightens skin and reduces pigmentation.',
    price: '₹799',
    image: 'https://source.unsplash.com/featured/?serum'
  },
  {
    name: 'AcneAway Cleanser',
    description: 'Controls oil and clears breakouts effectively.',
    price: '₹499',
    image: 'https://source.unsplash.com/featured/?cleanser'
  }
];

function RecommendationPage() {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');
  const [showProducts, setShowProducts] = useState(false);

  const handleAsk = () => {
    setResponse('Here are some product suggestions based on your concern...');
    setShowProducts(true);
  };

  const handleClear = () => {
    setQuery('');
    setResponse('');
    setShowProducts(false);
  };

  return (
    <div className="recommendation-wrapper">
      <div className="recommendation-box">
        <header className="recommendation-header">
          <h1>DermaBot</h1>
          <p>Your AI-powered skincare companion ✨</p>
        </header>

        <div className="chat-input-area">
          <textarea
            placeholder="Ask me your skincare query..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          ></textarea>
          <div className="button-group">
            <button onClick={handleAsk}>Ask</button>
            <button onClick={handleClear} className="clear-btn">Clear</button>
          </div>
        </div>

        {response && (
          <div className="response-area">
            <p>{response}</p>
          </div>
        )}

        {showProducts && (
          <div className="products-area">
            <h2>Recommended Products</h2>
            <div className="product-grid">
              {sampleProducts.map((product, i) => (
                <ProductCard key={i} product={product} />
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default RecommendationPage;
