import React from 'react';
import ChatInterface from '../components/ChatInterface';
import ProductCard from '../components/ProductCard';
import './RecommendationPage.css';

const sampleProducts = [
  {
    name: 'HydraGlow Serum',
    description: 'Brightens skin and reduces pigmentation.',
    price: 'â‚¹799',
    image: 'https://source.unsplash.com/featured/?serum'
  },
  {
    name: 'AcneAway Cleanser',
    description: 'Controls oil and clears breakouts effectively.',
    price: 'â‚¹499',
    image: 'https://source.unsplash.com/featured/?cleanser'
  },
  {
    name: 'Gentle Moisturizer',
    description: 'Hydrates sensitive skin without clogging pores.',
    price: 'â‚¹649',
    image: 'https://source.unsplash.com/featured/?moisturizer'
  }
];

function RecommendationPage() {
  return (
    <div className="recommendation-container">
      <header className="recommendation-header">
        <h1>Welcome to DermaBot</h1>
        <p>Your AI-powered skincare companion ðŸŒ¿</p>
      </header>

      <section className="chat-section">
        <ChatInterface />
      </section>

      <section className="product-section">
        <h2>Recommended for You</h2>
        <div className="product-grid">
          {sampleProducts.map((product, index) => (
            <ProductCard key={index} product={product} />
          ))}
        </div>
      </section>
    </div>
  );
}

export default RecommendationPage;
