import React from 'react';
import './ProductCard.css';

function ProductCard({ product }) {
  // Use product.image if present, otherwise generate from name
  const imageUrl = product.image || `https://source.unsplash.com/featured/?${encodeURIComponent(product.name)}`;
  return (
    <div className="product-card">
      <img src={imageUrl} alt={product.name} />
      <h3>{product.name}</h3>
    </div>
  );
}

export default ProductCard;
