import React, { useState } from 'react';
import ProductCard from '../components/ProductCard';
import './RecommendationPage.css';
import { getRecommendation } from '../services/api';

function RecommendationPage({ userProfile }) {
  const [query, setQuery] = useState('');
  const [response, setResponse] = useState('');
  const [products, setProducts] = useState([]);
  const [showProducts, setShowProducts] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAsk = async () => {
    if (!userProfile) {
      setError('Please fill out your user profile first.');
      return;
    }
    setLoading(true);
    setError('');
    setShowProducts(false);
    setResponse('');
    setProducts([]);
    try {
      // Send the userProfile object to the backend as user_id (or adjust as needed)
      const result = await getRecommendation(query, userProfile);
      if (result.error) {
        setError(result.error);
      } else {
        setResponse(result.explanation || '');
        // If routine exists, show as products; else, show the best product
        if (Array.isArray(result.routine) && result.routine.length > 0) {
          setProducts(result.routine.map(r => ({
            ...r.product,
            description: r.explanation || r.reasoning || '',
            name: r.product?.product_name || r.product?.name || '',
            price: r.product?.real_time_price || r.product?.price || '',
            image: r.product?.image || 'https://source.unsplash.com/featured/?skincare',
          })));
        } else if (result.product) {
          setProducts([{
            ...result.product,
            description: result.explanation || '',
            name: result.product.product_name || result.product.name || '',
            price: result.product.real_time_price || result.product.price || '',
            image: result.product.image || 'https://source.unsplash.com/featured/?skincare',
          }]);
        }
        setShowProducts(true);
      }
    } catch (err) {
      setError('Failed to fetch recommendation.');
    } finally {
      setLoading(false);
    }
  };

  const handleClear = () => {
    setQuery('');
    setResponse('');
    setProducts([]);
    setShowProducts(false);
    setError('');
  };

  return (
    <div className="recommendation-wrapper">
      <div className="recommendation-box">
        <header className="recommendation-header">
          <h1>DermaBot</h1>
          <p>Your AI-powered skincare companion ✨</p>
        </header>

        <div className="chat-input-area">
          <textarea
            placeholder="Ask me your skincare query..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          ></textarea>
          <div className="button-group">
            <button onClick={handleAsk} disabled={loading || !query.trim()}>{loading ? 'Loading...' : 'Ask'}</button>
            <button onClick={handleClear} className="clear-btn">Clear</button>
          </div>
        </div>

        {error && (
          <div className="response-area error">
            <p>{error}</p>
          </div>
        )}

        {response && !error && (
          <div className="response-area">
            <p>{response}</p>
          </div>
        )}

        {showProducts && products.length > 0 && (
          <div className="products-area">
            <h2>Recommended Products</h2>
            <div className="product-grid">
              {products.map((product, i) => (
                <ProductCard key={i} product={product} />
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default RecommendationPage;
